import React, { useState } from 'react';
import '../Nav/Nav.css';

function Navigation() {
  const [currentPage, setCurrentPage] = useState('Home'); // 현재 페이지 상태 관리

  const navigateTo = (page) => {
    setCurrentPage(page); // 페이지 변경 시 상태 업데이트
  };

  return (
    <div className="navigation">
      <ul>
        <li
          className={currentPage === 'Home' ? 'active' : ''}
          onClick={() => navigateTo('Home')}
        >
          Home
        </li>
        <li
          className={currentPage === 'Mypage' ? 'active' : ''}
          onClick={() => navigateTo('Mypage')}
        >
          Home &gt; Mypage
        </li>
        <li
          className={currentPage === 'Order' ? 'active' : ''}
          onClick={() => navigateTo('Order')}
        >
          Order
        </li>
        <li
          className={currentPage === 'Cart' ? 'active' : ''}
          onClick={() => navigateTo('Cart')}
        >
          Cart
        </li>
        <li
          className={currentPage === 'Qna' ? 'active' : ''}
          onClick={() => navigateTo('Qna')}
        >
          Qna
        </li>
        <li
          className={currentPage === 'Store' ? 'active' : ''}
          onClick={() => navigateTo('Store')}
        >
          Store
        </li>
      </ul>
    </div>
  );
}

export default Navigation;
