import React from "react";
import "./QnaModal.css";
import { Link } from 'react-router-dom';
// import { useState } from "react";

function QnaModal({ closeModal }) {

    useEffect(() => {
        // 이벤트 핸들러 함수
        const handleClickOutside = (event) => {
            // mousedown 이벤트가 발생한 영역이 모달창이 아닐 때, 모달창 제거 처리
            if (modalRef.current && !modalRef.current.contains(event.target)) {
                closeModal();
            }
        };

        // 이벤트 핸들러 등록
        document.addEventListener('mousedown', handleClickOutside);
        // document.addEventListener('touchstart', handler); // 모바일 대응

        return () => {
            // 이벤트 핸들러 해제
            document.removeEventListener('mousedown', handleClickOutside);
            // document.removeEventListener('touchstart', handler); // 모바일 대응
        };
    }, []);

    return (
        // 모달창을 useRef로 잡아준다.
        <div id="QnaModal_Background">
            <div div ref={modalRef} className="qnaModal_container" onClick={closeModal} >
                <div className="Modal_container2">
                    <img onClick={closeModal} className="madal_close_rotate" src="./images/search_X.png" alt="search_x" />
                    <fieldset onClick={handleModalClick}>
                        <legend></legend>
                        <input name="keyword" className="inputTypeText" placeholder=""
                            value={inputValue} type="text" onChange={handleInputChange}
                            onClick={handleModalClick} />
                        <img src="./images/search_icon.png" alt="검색" className="search_btn" onClick={handleSearchButtonClick} />
                    </fieldset>
                </div>
            </div >


// 내부 글쓰기
            <div className="path">
                <div>
                    <div className='qna_writeArea'>
                        <h2>Q & A</h2>
                    </div>

                    <form className="qna_write_info" action="" method="post">
                        <table className="qna_board_table">
                            <caption className="qna_writename">자유게시판 글쓰기</caption>
                            <tbody>
                                <tr>
                                    <th scope="row"><label for="qna_bID">아이디</label></th>
                                    <td className="qna_writeid"><input type="text" name="bID" id="qna_bID" /></td>
                                </tr>

                                <tr>
                                    <th scope="row"><label for="qna_bPassword">비밀번호</label></th>
                                    <td className="qna_writepassword"><input type="text" name="bPassword" id="qna_bPassword" /></td>
                                </tr>

                                <tr>
                                    <th scope="row"><label for="qna_bTitle">제목</label></th>
                                    <td className="qna_writetitle"><input type="text" name="bTitle" id="qna_bTitle" /></td>
                                </tr>

                                <tr>
                                    <th scope="row"><label for="qna_bContent">내용</label></th>
                                    <td className="qna_writecontent"><textarea name="bContent" id="qna_bContent"></textarea></td>
                                </tr>
                            </tbody>

                        </table>

                        <div class="qna_btnSet">
                            <button type="submit" className="qna_writesubmit_btn">작성</button>
                            <Link to="../Qna.jsx" className="qna_writelist_btn">목록</Link>
                        </div>


                    </form >
                </div>
            </div >

        </div > //QnaModal_Background
    )
};

export default QnaModal;
