import React, { useState } from 'react';
import './Qna.css';
// import React from 'react';
// import { Link } from 'react-router-dom';

const QnaFilter = ({ handleFilter }) => {
    const [category, setCategory] = useState("1");
    const [searchDate, setSearchDate] = useState("period");
    const [searchKey, setSearchKey] = useState("subject");
    const [searchValue, setSearchValue] = useState("");

    const handleCategoryChange = (e) => {
        setCategory(e.target.value);
    };

    const handleSearchDateChange = (e) => {
        setSearchDate(e.target.value);
    };

    const handleSearchKeyChange = (e) => {
        setSearchKey(e.target.value);
    };

    const handleSearchValueChange = (e) => {
        setSearchValue(e.target.value);
    };

    const handleSearch = () => {
        const filters = {
            category,
            searchDate,
            searchKey,
            searchValue
        };
        handleFilter(filters);
    };


    return (
        <div className='qna_filter_container'>
            <div className="qna_board_sort">
                <select name="board_category" value={category} onChange={handleCategoryChange}>
                    <option value="1">전체</option>
                    <option value="2">상품문의</option>
                    <option value="3">배송문의</option>
                    <option value="4">주문/결제</option>
                    <option value="5">취소문의</option>
                    <option value="6">반품/교환</option>
                    <option value="7">환불문의</option>
                    <option value="8">재입고문의</option>
                    <option value="9">기타문의</option>
                    <option value="10"></option>
                </select>
                <form className="qna_board_find">
                    <fieldset>
                        <legend>게시물 검색</legend>
                        <select name="search_date" value={searchDate} onChange={handleSearchDateChange}>
                            {/* ... */}
                            <option value="period">기간</option>
                            <option value="all">전체</option>
                            <option value="week">일주일</option>
                            <option value="month">한달</option>
                            <option value="month3">세달</option>
                        </select>
                        <select name="search_key" value={searchKey} onChange={handleSearchKeyChange}>
                            <option value="subject">제목</option>
                            <option value="content">내용</option>
                            <option value="writer_name">작성자</option>
                            <option value="product">상품정보</option>
                        </select>
                        <input type="text" value={searchValue} onChange={handleSearchValueChange} />
                        <button type="button" className="qna_board_find_btn" onClick={handleSearch}>찾기</button>
                    </fieldset>
                </form>
            </div>
        </div>
    );
};
export default QnaFilter;
