import './Qna.css';
import { Link } from 'react-router-dom';
import Pagination from "../../components/Pagination/Pagination";
import QnaFilter from './QnaFilter';
import QnaData from './QnaData'; // 새로 추가된 import
import React, { useState } from 'react'; // 이 부분을 수정하지 않았습니다.


const QnaPage = () => {
    const [filteredQna, setFilteredQna] = useState([]); // 상태로 필터링된 Q&A 게시물을 관리

    const handleFilter = (filters) => {
        // 실제 필터링 로직을 구현하여 filteredResults 배열에 필터링된 Q&A 게시물을 저장합니다.
        const filteredResults = qnaData.filter((qnaItem) => {
            // 필터링 로직을 적용하여 결과를 반환합니다.
            // filters 객체를 활용하여 필터링 조건을 처리합니다.
            // 예시: qnaItem.category === filters.category && qnaItem.date >= filters.searchDate
        });

        setFilteredQna(filteredResults);
    };


    return (
        <div className="qna_title_container">
            <div className="qna_path">
                <span>현재 위치</span>
                <ol>
                    <li><Link to="/">홈</Link></li>
                    <li title="현재 위치">&gt; &nbsp;&nbsp;QnA</li>
                </ol>
            </div>
            <div className="qna_title">
                <h2>QnA</h2>
                <div className="txt_01">Ojoa 문의게시판입니다.</div>
            </div>

            <QnaFilter handleFilter={handleFilter} />

            {/* 필터링된 Q&A 게시물 렌더링 */}
            {filteredQna.map((qnaItem) => (
                <div key={qnaItem.id}>
                    <h3>{qnaItem.subject}</h3>
                    <p>{qnaItem.content}</p>
                    {/* ...추가적인 게시물 정보... */}
                </div>
            ))}
            <Pagination />
        </div>
    );
};
export default QnaPage;
